import React from 'react';
import Table from "./Table/Table";
import './App.css';
import Loader from "./Loader/Loader";
import Months from "./Months/Months";
import Departments from "./Departments/Departments";

class App extends React.Component {


  state ={
    isLoading: true,
    data: [],
    jan: {},
    feb: {},
    march: {},
    april: {},
    may: {},
    june: {},
    july: {},
    aug: {},
    sep: {},
    oct: {},
    nov: {},
    dec: {},
    department: [],
    resMonth:{},
    resDep:{}
  }

  monthClickHandler(index){
    switch (index) {
      case 0:
        this.setState({
          resMonth: this.state.jan
        });
        break;
      case 1:
        this.setState({
          resMonth: this.state.feb
        })
        break;
      case 2:
        this.setState({
          resMonth: this.state.march
        })
        break;
      case 3:
        this.setState({
          resMonth: this.state.april
        })
        break;
      case 4:
        this.setState({
          resMonth: this.state.may
        })
        break;
      case 5:
        this.setState({
          resMonth: this.state.june
        })
        break;
      case 6:
        this.setState({
          resMonth: this.state.july
        })
        break;
      case 7:
        this.setState({
          resMonth: this.state.aug
        })
        break;
      case 8:
        this.setState({
          resMonth: this.state.sep
        })
        break;
      case 9:
        this.setState({
          resMonth: this.state.oct
        })
        break;
      case 10:
        this.setState({
          resMonth: this.state.nov
        })
        break;
      case 11:
        this.setState({
          resMonth: this.state.dec
        })
        break;
    }
  }

  departClickHandler(index){
    console.log(index)
  }

  async componentDidMount() {
    const response = await fetch(` http://localhost:8080/reports`)
    const data = await response.json();
    let departName = [];
    let jan = [];
    let feb = [];
    let march = [];
    let april = [];
    let may = [];
    let month;

    data.forEach((item) => {
      departName.push(item.employee.department.name);
      month = new Date(item.date);
      if (month.getMonth() === 0) {
        jan.push(item);
      }
      if (month.getMonth() === 1) {
        feb.push(item);
      }
      if (month.getMonth() === 2) {
        march.push(item);
      }
      if (month.getMonth() === 3) {
        april.push(item);
      }
      if (month.getMonth() === 4) {
        may.push(item);
      }
    });
    const depart = Array.from(new Set(departName));
    this.setState({
      data,
      jan: {dataOfMonth:jan, countOfDays: 31},
      feb: {dataOfMonth:feb, countOfDays: 29},
      march: {dataOfMonth:march, countOfDays: 31},
      april: {dataOfMonth:april, countOfDays: 30},
      may: {dataOfMonth:may, countOfDays: 31},
      june: {dataOfMonth:[], countOfDays: 30},
      july: {dataOfMonth:[], countOfDays: 31},
      aug: {dataOfMonth:[], countOfDays: 31},
      sep: {dataOfMonth:[], countOfDays: 30},
      oct: {dataOfMonth:[], countOfDays: 31},
      nov: {dataOfMonth:[], countOfDays: 30},
      dec: {dataOfMonth:[], countOfDays: 31},
      department: depart,
      isLoading: false,
    })
  }
  render() {
    let days = [];
    for (let i = 0; i < this.state.jan.countOfDays; i++) {
      days.push(i+1);
    }
    const monthArr = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October",
      "November", "December"];
    let months = null;
    months = monthArr.map((month, index) => {
      return (
        <Months
            key={index}
            month={month}
            index={index}
            monthClick={this.monthClickHandler.bind(this, index)}
        />
    )});

    let department = null;
    department = this.state.department.map((depart, index) => {
      return (
        <Departments
            key={index}
            depart={depart}
            index={index}
            departClick={this.departClickHandler.bind(this, index)}
        />
      )
    })
    return (
        <div>
          <div className="btn-group" role="group" aria-label="Basic example">
          {months}
          </div>
          {this.state.isLoading
                ? <Loader />
                :
              <div>
              <div className="row">
                <div className="col-1">
                <div className="btn-group-vertical" role="group" aria-label="Basic example">
                  {department}
                </div>
                </div>
                <div className="col-11">
                <Table
                    jan={this.state.jan}
                    feb={this.state.feb}
                    marcj={this.state.march}
                    april={this.state.april}
                    may={this.state.may}
                    department={this.state.department[0]}
                    days={days}
                />
                </div>
              </div>
              </div>
          }
        </div>
    );
  }
}

export default App;
