import React from "react";

function col(index) {
    return (
        <th> {index} </th>
    )
}

export default props => {
    const data = props.jan.dataOfMonth;
    let result = [];
    let names = [];
    data.filter(
        item => item.employee.department.name == props.department
    )
        .forEach(item => {
        names.push([item.employee.surname,item.employee.name,item.employee.middle_name].join(" "))
    });
    names = Array.from(new Set(names));
    names.forEach( n => {
        let obj = {};
        data.filter(item =>
            [item.employee.surname,item.employee.name,item.employee.middle_name].join(" ") ===n)
            .forEach((res,index) => {
                obj['fio'] = n;
                obj['position'] = res.employee.position
                obj['tabNumber'] = res.employee.tab_number;
                obj[index] = res.status;
            })
        result.push(obj);
    })
    return (
        <React.Fragment>
        <table className="table">
            <thead>
                <tr>
                    <th>ФИО</th>
                    <th>Должность</th>
                    <th>Таб. номер</th>
                    {result.forEach((item, index) => {
                        for (let i = 0; i< props.jan.countOfDays; i++) {
                            console.log(i);
                            col(i+1);
                        }
                    })}
                </tr>
            </thead>
            {/*<tbody>*/}
            {/*{ props.data.map(item =>(*/}
            {/*    <tr key={item.id}>*/}
            {/*        <td>{item.id}</td>*/}
            {/*        <td>{item.firstName}</td>*/}
            {/*        <td>{item.lastName}</td>*/}
            {/*        <td>{item.email}</td>*/}
            {/*        <td>{item.phone}</td>*/}
            {/*    </tr>*/}
            {/*))}*/}
            {/*</tbody>*/}
        </table>
        </React.Fragment>
    )
}
