import React from "react";



export default class Table extends React.Component {

    constructor(props) {
        super(props);
    }

    col = () => {
        for (let i = 0; i < this.props.jan.countOfDays; i++) {
            return (
                <th>{i+1}</th>
            )
        }
    }

    render() {

        // const col = () => {
        //     for (let i = 0; i < this.props.jan.countOfDays; i++) {
        //         return (
        //             <th>{i+1}</th>
        //         )
        //     }
        // }


        const data = this.props.jan.dataOfMonth;
        let result = [];
        let names = [];
        data.filter(
            item => item.employee.department.name === this.props.department
        )
            .forEach(item => {
                names.push([item.employee.surname,item.employee.name,item.employee.middle_name].join(" "))
            });
        names = Array.from(new Set(names));
        names.forEach( n => {
            let obj = {};
            data.filter(item =>
                [item.employee.surname,item.employee.name,item.employee.middle_name].join(" ") ===n)
                .forEach((res,index) => {
                    obj['fio'] = n;
                    obj['position'] = res.employee.position
                    obj['tabNumber'] = res.employee.tab_number;
                    obj[index] = res.status;
                })
            result.push(obj);
        })
        return (
            <React.Fragment>
                <table className="table">
                    <thead>
                    <tr>
                        <th>ФИО</th>
                        <th>Должность</th>
                        <th>Таб. номер</th>
                        {this.col}
                    </tr>
                    </thead>
                    {/*<tbody>*/}
                    {/*{ props.data.map(item =>(*/}
                    {/*    <tr key={item.id}>*/}
                    {/*        <td>{item.id}</td>*/}
                    {/*        <td>{item.firstName}</td>*/}
                    {/*        <td>{item.lastName}</td>*/}
                    {/*        <td>{item.email}</td>*/}
                    {/*        <td>{item.phone}</td>*/}
                    {/*    </tr>*/}
                    {/*))}*/}
                    {/*</tbody>*/}
                </table>
            </React.Fragment>
        )
    }
}
