import React from 'react';
import Table from "./Table/Table";
import './App.css';
import Loader from "./Loader/Loader";
import Months from "./Months/Months";

class App extends React.Component {


  state ={
    isLoading: true,
    data: [],
    jan: {},
    feb: [],
    march: [],
    april: [],
    may: [],
    department: []
  }

  onMonthClick = (index) => {
    console.log(index)
  }

  async componentDidMount() {
    const response = await fetch(` http://localhost:8080/reports`)
    const data = await response.json();
    let departName = [];
    let jan = [];
    let feb = [];
    let march = [];
    let april = [];
    let may = [];
    let month;

    data.forEach((item) => {
      departName.push(item.employee.department.name);
      month = new Date(item.date);
      if (month.getMonth() === 0) {
        jan.push(item);
      }
      if (month.getMonth() === 1) {
        feb.push(item);
      }
      if (month.getMonth() === 2) {
        march.push(item);
      }
      if (month.getMonth() === 3) {
        april.push(item);
      }
      if (month.getMonth() === 4) {
        may.push(item);
      }
    });
    const depart = Array.from(new Set(departName));
    this.setState({
      data,
      jan: {dataOfMonth:jan, countOfDays: 31},
      feb: feb,
      march: march,
      april: april,
      may: may,
      department: depart,
      isLoading: false,
    })
  }
  render() {
    let days = [];
    for (let i = 0; i < this.state.jan.countOfDays; i++) {
      days.push(i+1);
    }
    return (
        <div className="container">
          <Months
              month={this.state.monthNow}
              monthClickHandler={this.onMonthClick.bind(this,index)}
          />
          {
            this.state.isLoading
                ? <Loader />
                : <Table
                    jan={this.state.jan}
                    feb={this.state.feb}
                    marcj={this.state.march}
                    april={this.state.april}
                    may={this.state.may}
                    department={this.state.department[0]}
                    days={days}
                />
          }
        </div>
    );
  }
}

export default App;
